INSERT INTO `careers` (`id`, `title`, `content`, `sort`, `status`, `date_added`, `date_update`) VALUES
(6, 'Accounting Assistant', '<p><b>Qualification</b></p><ul><li>Candidate must be a graduate of BS Accountancy or other related courses</li><li>Fresh Graduate or with at least 1 year experience in general accounting (AP Disbursement)</li><li>With at least knowledge using SAP or other accounting system</li><li>Must be a team player and has initiative</li><li>Must be highly analytical and has keen attention to details</li><li>Good communication skills both written and oral</li><li>Must be residing in/near G. Araneta Ave., QC.</li><li>Can start immediately</li></ul>', 0, 1, '2024-02-02 10:46:29', '2024-02-02 05:30:26'),
(7, 'Encoder/Digital Customer Care Specialist', '<p><b>Responsibilities:</b></p><ul><li>Monitors all online orders and transactions.</li><li>Knowledgeable in stocks and price updates.</li><li>Responds to customer queries (i.e. chaport) and status√Ç in a timely and accurate way, via phone, email or chat with coordination to respective customer care/logistics department.</li><li>Monitors and resolves customer complaints on any sites and reach out to provide assistance.</li><li>Maintains a positive, empathetic, and professional attitude toward customers at all times.</li><li>Maintains accurate inventory of the products posted on each sites.</li><li>Reports any issues/errors with regarding to postings, payments and system.</li><li>Collates and updates hospital and client database.</li></ul><p><b>Qualifications:</b></p><ul><li>Candidate must be a Graduate of Business related courses</li><li>At least 1-2 years work experience in the same field is required with the position</li><li>Must have a good communications skills</li><li>Creative thinker</li><li>Well-versed in making presentations</li><li>Must be a residing in/near Ortigas Pasig City</li></ul><p><br></p>', 0, 1, '2024-02-02 11:47:52', '2024-02-02 03:51:52'),
(8, 'Healthcare Product Sales Specialist', '<div><b style=\"font-size: 14px; text-align: var(--bs-body-text-align); color: rgb(119, 119, 119); font-family: poppins, sans-serif; background-color: var(--bs-card-bg);\">Duties and Responsibilities:</b></div><ul><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">Maintaining and expanding business with existing customers and creating new business opportunities.</font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">Should be aggressive, self motivated and can work under minimum supervision.</font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">Manages the area of responsibilities even under pressure.</font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">Promotes the product effectively through proper and timely implementation of the marketing strategies and its subsequent action plans.</font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">Develops and maintains good public relations with purchasers, doctors, pharmacist and end-users relevant to the business.</font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">Achieves sales objectives in his/her territory.</font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\"><b>Qualifications:</b></font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">Candidate must be a graduate of any four-year course</font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">At least 1-2 years extensive work experience in the same field is required with the position.</font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">Must have average communications skills.</font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">Enthusiast and a team-player.</font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">Client-oriented.</font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">With pleasing personality.</font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">Well-versed in making presentations.</font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">Must be residing in NCR areas.</font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">Willing to do field work.</font></li><li style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\">Areas Vacant</font></li></ul><p style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\"><b>QC, Manila, Rizal</b></font></p><p style=\"line-height: 1.7; margin: 10px 0px;\"><font color=\"#777777\" face=\"poppins, sans-serif\"><b>Pasig, Mandaluyong, Makati</b></font></p>', 0, 1, '2024-02-02 11:54:08', '2024-02-02 05:20:41'),
(9, ' HR Assistant', '<p><span style=\"white-space-collapse: preserve;\"><b>REQUIREMENTS</b></span></p><ul><li>Candidate must be a graduate of Psychology, HRDM or other related courses</li><li>With 2-3 years experience especially in Recruitment, Employee and Labor Relations.</li><li>Preferably knowledgeable in ISO 9001:2015 documentation or from ISO certified Company.</li><li>Must be highly analytical and has keen attention to details</li><li>Good communication skills both written and oral</li><li>Must be living in/near Ortigas, Pasig City.</li><li>Can start immediately</li></ul>', 0, 1, '2024-02-02 11:56:24', '2024-02-02 03:56:24');
